package test;

public class ForLoopEvenNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		System.out.println("Display Even number using For Loop");
		for(i=0; i<10;i+=2)
		{
			System.out.println(" "+i);
		}
		
		int j;
		System.out.println("Display Even number in reverse using For Loop");
		for(j=10; j>0;j-=2)
		{
			System.out.println(" "+j);
		}
	}
	}


